import axios from 'axios';
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
//import myProduct from '../../../backend/models/Product';
//import logo from '../images/pos.png';


  /* Product */
  class Product extends Component {
    constructor(props) {
      super(props);
      this.state = {
        qty: this.props.quantity
      };
      this.add = this.add.bind(this);
      this.subtract = this.subtract.bind(this);
      this.showInfo = this.showInfo.bind(this);
    }

    add() {
      this.setState({
        qty: this.state.qty + 1
      });
      this.props.handleTotal(this.props.price);
    }

    subtract() {
      this.setState({
        qty: this.state.qty - 1
      });
      this.props.handleTotal(-this.props.price);
      //console.log(this.state.qty);
    }

    showInfo() {
      this.props.handleShow(this.props.info);
    }

    componentDidMount() {
      console.log('inside product did mount');
      let temp=( {
        _id:'',
        name: this.props.name,
        price: 100,
        image: '',
        info: 'new product',
        quantity:this.props.price

    });
    axios.post('/search/order/edit',{
        id:this.props.userId,
        _id:this.props.orderId,
        productID:this.props.productId,
        quantity:this.state.qty,
        product:temp

        })
    .then(res => {
        //console.log(this.props.match.params.id);
        //this.setState({productList:res.data[0].products});
        //this.setState({total:res.data[0].total});
        console.log(res.data);
    })
    .catch(err => {
        console.log('error occured!'); 
    });



    }

    render() {
        console.log("inside product render");
        // if(this.state.qty==null){
        //     return(<div>Loading...</div>);
        // }
        let temp=( {
            _id:'',
            name: this.props.name,
            price: 100,
            image: '',
            info: 'new product',
            quantity:this.props.price

        });
        axios.post('/search/order/edit',{
            id:this.props.userId,
            _id:this.props.orderId,
            productID:this.props.productId,
            quantity:this.state.qty,
            product:temp

            })
        .then(res => {
            //console.log(this.props.match.params.id);
            //this.setState({productList:res.data[0].products});
            //this.setState({total:res.data[0].total});
            //console.log(res.data);
        })
        .catch(err => {
            console.log('error occured!'); 
        });

      return (
        <div>
          <div className="row form-group">
            <div className="col-sm-10">
              <h4>{this.props.name}: ${this.props.price}</h4>
            </div>
            <div className="col-sm-2 text-right">qty: {this.state.qty}</div>
          </div>
          <div className="row btn-toolbar">
            <div className="col-6">
              <button className="btn btn-outline-primary" onClick={this.showInfo}>
                show info
              </button>
            </div>
            <div className="col-6 text-right">
              <button className="btn btn-outline-primary" onClick={this.add}>
                +1
              </button>
              <button className="btn btn-outline-primary" onClick={this.subtract} disabled={this.state.qty < 1}>
                -1
              </button>
            </div>
          </div>
          <hr />
        </div>
      );
    }
  }

  /* Total */
  class Total extends Component {
    // constructor(props) {
    //   super(props);
    // }
    render() {
      let total = this.props.total.toFixed(2);
      let tax = (this.props.total * 0.15).toFixed(2);
      let totalIncTax = (+total + +tax).toFixed(2);
      let mystyle = {
        borderTop: "1px solid #ddd",
        marginTop: "10px"
      };

    console.log('inside total render:',{total});  
    axios.post('/search/order/total',{
        id:this.props.userId,
        _id:this.props.orderId,
        total:total

        })
    .then(res => {

        //console.log(res.data);
    })
    .catch(err => {
        console.log('error occured!'); 
    });

      return (
        <div style={{"marginTop": "30px", "backgroundColor":"#F6F6F6","padding": "10px"}}>
          <h3 className="row" style={{ fontWeight: 400 }}>
            <span className="col-6">total price:</span>
            <span className="col-6 text-right">${total}</span>
          </h3>
          <h3 className="row" style={{ fontWeight: 400 }}>
            <span className="col-6">tax (15%):</span>
            <span className="col-6 text-right">${tax}</span>
          </h3>
          <h3 className="row" style={mystyle}>
            <span className="col-6">tota inc tax:</span>
            <span className="col-6 text-right">${totalIncTax}</span>
          </h3>

        </div>
      );
    }
  }

  /* ProductForm */
  class ProductForm extends Component {
    submit(e) {
      e.preventDefault();
      var product = {
        name: this.refs.name.value,
        price: Number(this.refs.price.value),
        info: this.refs.info.value,
        quantity:100
      };
      console.log(this.refs.name.value, this.refs.price.value);
      this.props.handleProduct(product);
      this.refs.name.value = "";
      this.refs.price.value = 0;
      this.refs.info.value = "";
    }

    render() {
      return (
        <form onSubmit={this.submit.bind(this)}>
          <h3>Add to Cart</h3>
          <div className="row form-group">
            <label className="col-sm-2  col-sm-form-label" >Name:</label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                ref="name"
                placeholder="e.g.) android"
                required
              />
            </div>
          </div>

