import axios from 'axios';
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
//import logo from '../images/pos.png';


  /* Product */
  class Product extends Component {
    constructor(props) {
      super(props);
      this.state = {
        qty: this.props.quantity
      };
      this.add = this.add.bind(this);
      this.subtract = this.subtract.bind(this);
      this.showInfo = this.showInfo.bind(this);
    }

    add() {
      this.setState({
        qty: this.state.qty + 1
      });
      this.props.handleTotal(this.props.price);
    }

    subtract() {
      this.setState({
        qty: this.state.qty - 1
      });
      this.props.handleTotal(-this.props.price);
      //console.log(this.state.qty);
    }

    showInfo() {
      this.props.handleShow(this.props.info);
    }

    componentDidMount() {

        setTimeout(() => {            
            axios.post('/search/order/edit',{
                id:this.props.userId,
                _id:this.props.orderId,
                productID:this.props.productId
                })
            .then(res => {
                //console.log(this.props.match.params.id);
                //this.setState({productList:res.data[0].products});
                //this.setState({total:res.data[0].total});
                console.log(res.data);
            })
            .catch(err => {
                console.log('error occured!'); 
            });

        }, 100);
        }

    render() {

      return (
        <div>
          <div className="row form-group">
            <div className="col-sm-10">
              <h4>{this.props.name}: ${this.props.price}</h4>
            </div>
            <div className="col-sm-2 text-right">qty: {this.state.qty}</div>
          </div>
          <div className="row btn-toolbar">
            <div className="col-6">
              <button className="btn btn-outline-primary" onClick={this.showInfo}>
                show info
              </button>
            </div>
            <div className="col-6 text-right">
              <button className="btn btn-outline-primary" onClick={this.add}>
                +1
              </button>
              <button className="btn btn-outline-primary" onClick={this.subtract} disabled={this.state.qty < 1}>
                -1
              </button>
            </div>
          </div>
          <hr />
        </div>
      );
    }
  }

  /* Total */
  class Total extends Component {
    // constructor(props) {
    //   super(props);
    // }
    render() {
      let total = this.props.total.toFixed(2);
      let tax = (this.props.total * 0.15).toFixed(2);
      let totalIncTax = (+total + +tax).toFixed(2);
      let mystyle = {
        borderTop: "1px solid #ddd",
        marginTop: "10px"
      };
      return (
        <div style={{"marginTop": "30px", "backgroundColor":"#F6F6F6","padding": "10px"}}>
          <h3 className="row" style={{ fontWeight: 400 }}>
            <span className="col-6">total price:</span>
            <span className="col-6 text-right">${total}</span>
          </h3>
          <h3 className="row" style={{ fontWeight: 400 }}>
            <span className="col-6">tax (15%):</span>
            <span className="col-6 text-right">${tax}</span>
          </h3>
          <h3 className="row" style={mystyle}>
            <span className="col-6">tota inc tax:</span>
            <span className="col-6 text-right">${totalIncTax}</span>
          </h3>

        </div>
      );
    }
  }

  /* ProductForm */
  class ProductForm extends Component {
    submit(e) {
      e.preventDefault();
      var product = {
        name: this.refs.name.value,
        price: Number(this.refs.price.value),
        info: this.refs.info.value
      };
      console.log(this.refs.name.value, this.refs.price.value);
      this.props.handleProduct(product);
      this.refs.name.value = "";
      this.refs.price.value = 0;
      this.refs.info.value = "";
    }

    render() {
      return (
        <form onSubmit={this.submit.bind(this)}>
          <h3>Add to Cart</h3>
          <div className="row form-group">
            <label className="col-sm-2  col-sm-form-label">name:</label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                ref="name"
                placeholder="e.g.) android"
                required
              />
            </div>
          </div>

          <div className="row form-group">
            <label className="col-sm-2  col-sm-form-label">price:</label>
            <div className="col-sm-10">
              <input
                type="number"
                className="form-control"
                ref="price"
                placeholder="e.g.) 100"
                required
              />
            </div>
          </div>

          <div className="row form-group">
            <label className="col-sm-2  col-sm-form-label">info:</label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                ref="info"
                placeholder="e.g.) product of google"
              />
            </div>
          </div>

          <div className="row form-group">
            <div className="offset-2 col-10">
              <button className="btn btn-outline-primary">create product</button>
            </div>
          </div>

          <hr />
        </form>
      );
    }
  }

  /* ProductList */
  class ProductList extends Component {
    constructor(props) {
      super(props);

      this.state = {
        total: 0,
        productList: "",
        products:{},
        orderId:'',
        userId:''
      };

        //this.props.name='yasod'
        //console.log(this.props.auth);

      this.createProduct = this.createProduct.bind(this);
      this.calculateTotal = this.calculateTotal.bind(this);
      this.showProduct = this.showProduct.bind(this);
    }

    componentDidMount() {
        if(!this.props.auth.isAuthenticated) {
            this.props.history.push('/login');
        }
        else{
        setTimeout(() => {
            // axios.post('/search/order',{})
            // .then(res => {
            //     this.setState({productList:res.data});
            //     console.log(this.state.productList);
            // })
            // .catch(err => {
            //     console.log('error occured!'); 
            // });
            // console.log('timeout');
            //this.setState({ productList: productList });

            axios.post('/search/individual',{

                id:this.props.auth.user.id,
                _id:this.props.match.params.id
                })
            .then(res => {

                this.setState({
                    productList:res.data[0].products,
                    total:res.data[0].total,
                    userId:this.props.auth.user.id,
                    orderId:this.props.match.params.id

                });
                console.log(this.state);

                //console.log(res.data);
            })
            .catch(err => {
                console.log('error occured!'); 
            });

        }, 100);
        }




    }

    createProduct(product) {
      this.setState({
        product: this.state.productList.push(product)
      });
      console.log(this.state.productList);
    }

    calculateTotal(price) {
      this.setState({
        total: this.state.total + price
      });
      console.log(this.state.total);
    }

    showProduct(info) {
      console.log(info);
      alert(info);
    }

    render() {
      if (!this.state.productList) return <p>loading...!!!!</p>;

      var component = this;
      var myState = component.props;
      var products = this.state.productList.map(function(product) {
        return (
          <Product
            name={product.name}
            price={product.price}
            info={product.info}
            quantity={product.quantity}
            handleShow={component.showProduct}
            handleTotal={component.calculateTotal}
            productId={product._id}
            userId={myState.auth.user.id}

          />
        );
      });

      return (
        <div>
          <ProductForm handleProduct={this.createProduct} />
          {products}
          <Total total={this.state.total} />
        </div>
      );
    }
  }

  const mapStateToProps = (state) => ({
    auth: state.auth
})

export default connect(mapStateToProps)( withRouter(ProductList));
