// Home.js
import axios from 'axios';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import logo from '../images/pos.png';
import { withRouter } from 'react-router-dom';
import { Link } from 'react-router-dom';
import Carousel from 'react-bootstrap/Carousel'

class ControlledCarousel extends Component {
    constructor(props, context) {
      super(props, context);

      this.handleSelect = this.handleSelect.bind(this);

      this.state = {
        index: 0,
        direction: null,
      };
    }

    handleSelect(selectedIndex, e) {
      this.setState({
        index: selectedIndex,
        direction: e.direction,
      });
    }

    render() {
      const { index, direction } = this.state;

      return (
        <Carousel
          activeIndex={index}
          direction={direction}
          onSelect={this.handleSelect}
        >
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=First slide&bg=373940"
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Second slide&bg=282c34"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="holder.js/800x400?text=Third slide&bg=20232a"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      );
    }
  }



class Home extends Component {

    constructor(){
        super();
        this.state=null;

    }

    componentWillMount() {
        if(!this.props.auth.isAuthenticated) {
            this.props.history.push('/login');
        }else{
            axios.post('/search',{})
            .then(res => {
                //this.setState(res.data);

            })
            .catch(err => {
                console.log('error occured!'); 
            });
            //console.log(this.props.auth.user.id);

            axios.post('/search/order',{id:this.props.auth.user.id})
            .then(res => {
                this.setState(res.data);
                //console.log(res.data);
            })
            .catch(err => {
                console.log('error occured!'); 
            });



        }

    }

    orderRender(){

        if(this.state != null){
            let temp = [];
            //console.log(this.state.length);
            const len=Object.keys(this.state).length;
            for (let i=0;i<len;i++){
                temp.push(<div key={i}>{this.myFunction(i)}</div>);
            }
            //console.log(temp);
            return(temp);

        }else{    
            return(<div>state null</div>);  

        }




    }

    myFunction(i = 0) {
        if (this.state != null){
            const productNames = [];

            if (this.state[i].products != null){

                let ordersLength = this.state[i].products.length;
                const products = this.state[i].products;

                for (let i=0;i<ordersLength;i++){
                    productNames.push(<div key={i}> <li>{products[i].name}</li></div>);
                }


            }


            console.log(this.state);
            return(


            <div className="card col-sm" style={{ height:'530px',width: '350px', marginLeft: '10px' ,marginTop: '40px'}}>
            <img src={this.state[i].image} className="card-img-top" alt="..."style={{ height:'200px',width:'280px', marginLeft: '20px' ,marginTop: '20px'}}/>
            <div className="card-body">
                <h5 className="card-title"><b>{this.state[i].name}</b></h5> 
                <h6 className="card-text"><b>Order Id:</b>{this.state[i]._id}</h6> 
                <div className="card-text"><h6><b>Items: </b><ol>{productNames}</ol></h6> 
                </div>
                <p className="card-text"><b>Order Total: $ {this.state[i].total}</b></p>
                {/* {this.state[i].id} */}
                {/* <a href="/orderdetails/?" className="btn btn-primary" >Order Details</a> */}
                <Link  className="btn btn-primary" to={`/orderdetails/${this.state[i]._id}`}>Order Details</Link>
        </div>
        </div>);

        }else{
            return(<div>state null</div>);            
        }



    }


    render() {
        const orders = this.orderRender();
        //const orders1 = this.myFunction(0);
        console.log('inside render');
        //console.log(this.props.auth.user.id);



        //console.log(this.state);
        //const data =[{"name":"test1"},{"name":"test2"}];
        //const listItems = data.map((d) => <li key={d.name}>{d.name}</li>);
        return (

            <div>
                 <ControlledCarousel/>

                  <div className='container'style={{marginTop:'10px'}}>
                <div className="row" >
                    <div className="logo">
                        <img src={logo} alt='Logo' left = "400" width="100" height="100" />
                    </div>
                </div>
                </div>

                <div className="card" style={{ width:'1050px',height: '70px', marginLeft: '0px' ,marginTop: '30px'}}>
                    <div className="card-body">
                       <h3>Open Orders.</h3> 
                    </div>
                </div>

                {/* {orders} */}
                <div className='row'>{orders}</div>
                {/* <div className='row'><Order/><Order/><Order/><Order/></div> */}



            </div>





        );
    }
}


const mapStateToProps = (state) => ({
    auth: state.auth
})

export default connect(mapStateToProps)(withRouter(Home));
