// Home.js
import axios from 'axios';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import logo from '../images/pos.png';
//import { Helmet } from "react-helmet"
//import propduct



class Home extends Component {

    constructor() {
        super();
        console.log('hi');
        //this.state={name:''}
        this.state={};
        //this.props.name='yasod'
        //console.log(this.props.auth);
        axios.post('/search',{})
            .then(res => {
                this.setState(res.data);
                console.log(this.state);
            })
            .catch(err => {
                console.log('error occured!'); 
            });

    }

    componentDidMount() {
        if(!this.props.auth.isAuthenticated) {
            this.props.history.push('/login');
        }
    }
    test(){
        return (
            <div>
            <div className="card" style={{ width: '300px', marginLeft: '20px' ,marginTop: '40px'}}>
                    <img src={this.state.image} className="card-img-top" alt="..."/>
                    <div className="card-body">
                        <h5 className="card-title">{this.state.name}</h5> 
                        <p className="card-text">Product Category: {this.state.category}</p>
                        <p className="card-text">Price: {this.state.price}</p>
                        <a href="/" className="btn btn-primary">Order Details</a>
                    </div>
                </div>
            </div>


        );


    }



    render() {


        //console.log(this.props.name);
        const data =[{"name":"test1"},{"name":"test2"}];
        const listItems = data.map((d) => <li key={d.name}>{d.name}</li>);
        return (

            <div>
                  <div className='container'style={{marginTop:'10px'}}>
                <div className="row" >
                    <div className="logo">
                        <img src={logo} alt='Logo' left = "400" width="100" height="100" />
                    </div>
                </div>
                </div>

                <div className="card" style={{ height: '70px', marginLeft: '0px' ,marginTop: '30px'}}>
                    <div className="card-body">
                       <h3>Open Orders.</h3> 
                    </div>
                </div>

                <div className="card" style={{ width: '300px', marginLeft: '20px' ,marginTop: '40px'}}>
                    <img src={this.state.image} className="card-img-top" alt="..."/>
                    <div className="card-body">
                        <h5 className="card-title">{this.state.name}</h5> 
                        <p className="card-text">Product Category: {this.state.category}</p>
                        <p className="card-text">Price: {this.state.price}</p>
                        <a href="/" className="btn btn-primary">Order Details</a>
                    </div>
                </div>
                <div>{listItems}</div>
                <test/>


            </div>





        );
    }
}


const mapStateToProps = (state) => ({
    auth: state.auth
})

export default connect(mapStateToProps)(Home);
