// Home.js
import axios from 'axios';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import logo from '../images/pos.png';

//import { Helmet } from "react-helmet"
//import propduct

// class Order extends Component {

//     render(){



//         return(

//            <div>{test12} </div>    

//         );
//     }




// }

class Home extends Component {

    constructor(){
        super();
        this.state=null;
        //     {name:'',
        //     price:0,
        //     image:'',
        //     category:''}

        // };


    }

    componentDidMount() {
        if(!this.props.auth.isAuthenticated) {
            this.props.history.push('/login');
        }else{
            axios.post('/search',{})
            .then(res => {
                //this.setState(res.data);

            })
            .catch(err => {
                console.log('error occured!'); 
            });
            console.log(this.props.auth.user.id);

            axios.post('/search/order',{id:this.props.auth.user.id})
            .then(res => {
                this.setState(res.data);
                console.log(res.data);
            })
            .catch(err => {
                console.log('error occured!'); 
            });



        }

    }

    orderRender(){
        if(this.state!=null){
            let temp;
            console.log(this.state.length);
            for (let i=0;i<this.state.length;i++){
                temp += <div>{this.myFunction(0)}</div>
            }
            console.log(temp);
            return(temp);



        }else{


            return(<div>state null</div>);  

        }




    }

    myFunction(i=0) {
        if (this.state!=null){
            return(

            <div className="card col-sm" style={{ width: '300px', marginLeft: '20px' ,marginTop: '40px'}}>
            <img src={this.state[i].image} className="card-img-top" alt="..."/>
            <div className="card-body">
                <h5 className="card-title">{this.state[i].name}</h5> 
                {/* <p className="card-text">Ordered items: {this.state[i].category}</p> */}
                <p className="card-text">Total: {this.state[i].total}</p>
                <a href="/orderdetails" className="btn btn-primary">Order Details</a>
        </div>
        </div>);

        }else{
            return(<div>state null</div>);            
        }



    }


    render() {
        const orders = this.orderRender();
        //const orders1 = this.myFunction(1);
        console.log('inside render');
        console.log(this.props.auth.user.id);



        //console.log(this.state);
        //const data =[{"name":"test1"},{"name":"test2"}];
        //const listItems = data.map((d) => <li key={d.name}>{d.name}</li>);
        return (

            <div>
                  <div className='container'style={{marginTop:'10px'}}>
                <div className="row" >
                    <div className="logo">
                        <img src={logo} alt='Logo' left = "400" width="100" height="100" />
                    </div>
                </div>
                </div>

                <div className="card" style={{ height: '70px', marginLeft: '0px' ,marginTop: '30px'}}>
                    <div className="card-body">
                       <h3>Open Orders.</h3> 
                    </div>
                </div>


                <div className='row'>{orders}</div>
                {/* <div className='row'><Order/><Order/><Order/><Order/></div> */}



            </div>





        );
    }
}


const mapStateToProps = (state) => ({
    auth: state.auth
})

export default connect(mapStateToProps)(Home);
